module Day7
  ( parseInput
  , leafsSet
  , namesSet
  , bottom
  , day7Input
  ) where

import Data.List
import Data.Maybe

parseInput :: String -> [(String,Int,[String])]
parseInput s = map parseLine $ lines s

parseLine :: String -> (String,Int,[String])
parseLine l = (head $ words l, count, leafs)
  where leafs
          | "->" `isInfixOf` l = parseLeafs l
          | otherwise          = []
        count = parseCount l

parseLeafs :: String -> [String]
parseLeafs s = leafs
  where gtIndex = fromJust $ elemIndex '>' s
        leafsPart = drop (gtIndex+1) s
        stripCommas = filter (/= ',') leafsPart
        leafs = words stripCommas

parseCount :: String -> Int
parseCount s = read countString :: Int
  where openBraceIndex = fromJust $ elemIndex '(' s
        closeBraceIndex = fromJust $ elemIndex ')' s
        countLen = closeBraceIndex - openBraceIndex - 1
        afterOpenBrace = drop (openBraceIndex + 1) s
        countString = take countLen afterOpenBrace

leafsSet :: [(String,Int,[String])] -> [String]
leafsSet xs = nub $ concat $ map leafs xs
  where leafs (n,c,lfs) = lfs

namesSet :: [(String,Int,[String])] -> [String]
namesSet = map name
  where name (n, c, lfs) = n

bottom :: [(String,Int,[String])] -> String
bottom xs = head $ (namesSet xs) \\ (leafsSet xs)

